import React, { useState, useEffect } from 'react';
import AdminService from '../../../../../Services/admin_service/AdminService';
import Constants from '../../../../Constants';
import { CDBDataTable } from 'cdbreact';

const CompanyUsersComponent = () => {
    const [searchUsers, setSearchUsers] = useState([]); // Holds the list of candidates
    const [selectedCompany, setSelectedCompany] = useState(""); // Holds the selected company name
    const [showEnrolledCandidates, setShowEnrolledCandidates] = useState(false); // Controls visibility of the candidates table
    const [loadingBar, setLoadingBar] = useState(false); // Controls the loading state
    const [errorMessageDiv, setErrorMessageDiv] = useState(false); // Controls error message visibility
    const [errorMessage, setErrorMessage] = useState(""); // Holds the error message
    const [companyName, setCompanyName] = useState(""); // State for the company name input

    // Function to fetch users based on the company name entered
    const fetchUsersWithCompanyName = async () => {
        if (!companyName.trim()) {
            displayErrorMessage("Please enter a valid company name");
            return;
        }

        setLoadingBar(true);
        const requestPayload = { companyName }; // Payload includes company name
        try {
            const responseData = await AdminService.fetchCandidatesByCompany(requestPayload);
            console.log(responseData); // Log the response data

            if (responseData.length === 0) {
                displayErrorMessage("No candidates found for this company");
            } else {
                setSearchUsers(responseData); // Set searchUsers with the fetched data
                setSelectedCompany(companyName); // Set selected company name
                setShowEnrolledCandidates(true); // Show the candidates table
            }
        } catch (error) {
            console.error(error.message);
            handleFetchCandidatesErrors(error.message);
        } finally {
            setLoadingBar(false); // Hide loading state
        }
    };

    // Error handling for the fetch function
    const handleFetchCandidatesErrors = (errorStatus) => {
        if (Constants.INVALID_REQUEST_FIELD === errorStatus)
            displayErrorMessage("The company name is invalid");
        else if (Constants.BREEZE_DATABASE_EXCEPTION === errorStatus)
            displayErrorMessage("Sorry, Our service is currently down");
        else displayErrorMessage("Could not process your request");
    };

    // Display error message
    const displayErrorMessage = (message) => {
        setErrorMessage(message);
        setErrorMessageDiv(true);
        setTimeout(() => {
            setErrorMessage("");
            setErrorMessageDiv(false);
        }, Constants.SET_TIME_OUT_FOR_ERROR_MSG);
    };

    // Columns definition for the DataTable
    const columns = [
        { label: 'User ID', field: 'userId' },
        { label: 'First Name', field: 'userFirstname' },
        { label: 'Last Name', field: 'userLastname' },
       
    ];

    return (
        <div>
            <h1>Company Users</h1>

            {/* Display error message */}
            {errorMessageDiv && <div className="error-message">{errorMessage}</div>}

            {/* Input for company name */}
            <div style={{ marginBottom: "10px" }}>
                <label htmlFor="companyName" style={{ marginRight: "10px" }}>
                    Enter Company Name:
                </label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)} // Track input changes
                    placeholder="Enter company name"
                    style={{
                        padding: "5px",
                        fontSize: "14px",
                        borderRadius: "5px",
                        border: "1px solid #ccc",
                        marginRight: "10px",
                    }}
                />
                <button
                    onClick={fetchUsersWithCompanyName} // Trigger fetch
                    style={{
                        padding: "5px 10px",
                        fontSize: "14px",
                        borderRadius: "5px",
                        backgroundColor: "#007bff",
                        color: "#fff",
                        border: "none",
                        cursor: "pointer",
                    }}
                >
                    Search
                </button>
            </div>

            {/* Show loading indicator */}
            {loadingBar && <p>Loading...</p>}

            {/* Show enrolled candidates table if data is available */}
            {showEnrolledCandidates && (
                <div>
                    <h2>Enrolled Candidates for {selectedCompany}</h2>

                    {/* DataTable using CDBDataTable */}
                    <CDBDataTable
                        rows={searchUsers} // Correct state data for DataTable
                        columns={columns}
                        striped
                        bordered
                        hover
                    />
                </div>
            )}
        </div>
    );
};

export default CompanyUsersComponent;
