import React, { useState, useEffect } from 'react';
import AdminService from '../../../../../Services/admin_service/AdminService';
import Constants from '../../../../Constants';

const CompanyUsersComponent = () => {
    const [searchUsers, setSearchUsers] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState('');
    const [loadingBar, setLoadingBar] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [companyName, setCompanyName] = useState('');

    // Fetch users by company name
    const fetchUsersWithCompanyName = async () => {
        if (!companyName.trim()) {
            displayErrorMessage('Please enter a valid company name.');
            return;
        }

        setLoadingBar(true);
        setErrorMessage(''); // Clear previous error messages
        try {
            const requestPayload = { companyName };
            const responseData = await AdminService.fetchCandidatesByCompany(requestPayload);

            if (!responseData || responseData.length === 0) {
                displayErrorMessage('No candidates found for this company.');
                setSearchUsers([]); // Empty the users list
                return;
            }

            // Deduplicate users based on userId
            const uniqueData = responseData.reduce((acc, user) => {
                if (!acc.find((item) => item.userId === user.userId)) {
                    acc.push({
                        userId: user.userId,
                        userFirstname: user.userFirstname,
                        userLastname: user.userLastname,
                        phoneNumber: user.phoneNumber,
                        username: user.username,
                        userType: user.userType,
                        exponentType: user.exponentType,
                    });
                }
                return acc;
            }, []);
            
            // Update the searchUsers state with the unique data
            setSearchUsers(uniqueData);
            setSelectedCompany(companyName);
        } catch (error) {
            console.error(error.message);
            handleFetchCandidatesErrors(error.message);
        } finally {
            setLoadingBar(false);
        }
    };

    // Handle errors from fetching candidates
    const handleFetchCandidatesErrors = (errorStatus) => {
        if (errorStatus === Constants.INVALID_REQUEST_FIELD) {
            displayErrorMessage('The company name is invalid.');
        } else if (errorStatus === Constants.BREEZE_DATABASE_EXCEPTION) {
            displayErrorMessage('Sorry, our service is currently unavailable.');
        } else {
            displayErrorMessage('An unexpected error occurred. Please try again later.');
        }
    };

    // Display error message
    const displayErrorMessage = (message) => {
        setErrorMessage(message);
        setTimeout(() => setErrorMessage(''), Constants.SET_TIME_OUT_FOR_ERROR_MSG || 3000);
    };

    return (
        <div>
            <h1>Company Users</h1>

            {/* Display error message */}
            {errorMessage && <div className="error-message" style={{ color: 'red', marginBottom: '10px' }}>{errorMessage}</div>}

            {/* Input for company name and search button */}
            <div className="search-container" style={{ marginBottom: '20px' }}>
                <label htmlFor="companyName" style={{ marginRight: '10px' }}>Enter Company Name:</label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                    placeholder="Enter company name"
                    style={{
                        padding: '5px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        border: '1px solid #ccc',
                        marginRight: '10px',
                    }}
                />
                <button
                    onClick={fetchUsersWithCompanyName}
                    style={{
                        padding: '5px 10px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        cursor: 'pointer',
                    }}
                >
                    Search
                </button>
            </div>

            {/* Show loading message while fetching */}
            {loadingBar && <p>Loading...</p>}

            {/* Display data table if there are users */}
            {searchUsers.length > 0 ? (
                <div>
                    <h2>Enrolled Candidates for {selectedCompany}</h2>
                    <table className="styled-table" style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                        <thead>
                            <tr>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>#</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>User ID</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>First Name</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>Last Name</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>Phone Number</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>Username</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>User Type</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>Exponent Type</th>
                            </tr>
                        </thead>
                        <tbody>
                            {searchUsers.map((user, index) => (
                                <tr key={user.userId} style={{ borderBottom: '1px solid #ccc' }}>
                                    <td style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>{index + 1}</td>
                                    <td style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>{user.userId}</td>
                                    <td style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>{user.userFirstname}</td>
                                    <td style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>{user.userLastname}</td>
                                    <td style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>{user.phoneNumber}</td>
                                    <td style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>{user.username}</td>
                                    <td style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>{user.userType}</td>
                                    <td style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>{user.exponentType}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            ) : (
                !loadingBar && selectedCompany && <p>No users found for the company "{selectedCompany}".</p>
            )}
        </div>
    );
};

export default CompanyUsersComponent;
