import React, { useState } from 'react';
import AdminService from '../../../../../Services/admin_service/AdminService';
import Constants from '../../../../Constants';

const CompanyUsersComponent = () => {
    const [searchUsers, setSearchUsers] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState('');
    const [loadingBar, setLoadingBar] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [companyName, setCompanyName] = useState('');

    const convertToRoman = (num) => {
        const romanNumerals = [
            { value: 1000, numeral: 'M' },
            { value: 900, numeral: 'CM' },
            { value: 500, numeral: 'D' },
            { value: 400, numeral: 'CD' },
            { value: 100, numeral: 'C' },
            { value: 90, numeral: 'XC' },
            { value: 50, numeral: 'L' },
            { value: 40, numeral: 'XL' },
            { value: 10, numeral: 'X' },
            { value: 9, numeral: 'IX' },
            { value: 5, numeral: 'V' },
            { value: 4, numeral: 'IV' },
            { value: 1, numeral: 'I' },
        ];
        let result = '';
        for (const { value, numeral } of romanNumerals) {
            while (num >= value) {
                result += numeral;
                num -= value;
            }
        }
        return result;
    };

    const fetchUsersWithCompanyName = async () => {
        if (!companyName.trim()) {
            displayErrorMessage('Please enter a valid company name.');
            return;
        }

        setLoadingBar(true);
        setErrorMessage('');
        try {
            const requestPayload = { companyName };
            const responseData = await AdminService.fetchCandidatesByCompany(requestPayload);

            if (!responseData || responseData.length === 0) {
                displayErrorMessage('No candidates found for this company.');
                setSearchUsers([]);
                return;
            }

            const uniqueData = responseData.reduce((acc, user, index) => {
                if (!acc.find((item) => item.userId === user.userId)) {
                    acc.push({
                        romanIndex: convertToRoman(index + 1),
                        userId: user.userId,
                        userFirstname: user.userFirstname,
                        userLastname: user.userLastname,
                        username: user.username,
                        phoneNumberWithCountryCode: user.phoneNumberWithCountryCode,
                        gender: user.gender,
                    });
                }
                return acc;
            }, []);

            setSearchUsers(uniqueData);
            setSelectedCompany(companyName);
        } catch (error) {
            console.error(error.message);
            handleFetchCandidatesErrors(error.message);
        } finally {
            setLoadingBar(false);
        }
    };

    const handleFetchCandidatesErrors = (errorStatus) => {
        if (errorStatus === Constants.INVALID_REQUEST_FIELD) {
            displayErrorMessage('The company name is invalid.');
        } else if (errorStatus === Constants.BREEZE_DATABASE_EXCEPTION) {
            displayErrorMessage('Sorry, our service is currently unavailable.');
        } else {
            displayErrorMessage('An unexpected error occurred. Please try again later.');
        }
    };

    const displayErrorMessage = (message) => {
        setErrorMessage(message);
        setTimeout(() => setErrorMessage(''), Constants.SET_TIME_OUT_FOR_ERROR_MSG || 3000);
    };

    return (
        <div>
            <h1>Company Users</h1>

            {errorMessage && <div className="error-message" style={{ color: 'red', marginBottom: '10px' }}>{errorMessage}</div>}

            <div style={{ marginBottom: '10px' }}>
                <label htmlFor="companyName" style={{ marginRight: '10px' }}>
                    Enter Company Name:
                </label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                    placeholder="Enter company name"
                    style={{
                        padding: '5px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        border: '1px solid #ccc',
                        marginRight: '10px',
                    }}
                />
                <button
                    onClick={fetchUsersWithCompanyName}
                    style={{
                        padding: '5px 10px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        cursor: 'pointer',
                    }}
                >
                    Search
                </button>
            </div>

            {loadingBar && <p>Loading...</p>}

            {searchUsers.length > 0 ? (
                <div>
                    <h2>Enrolled Candidates for {selectedCompany}</h2>
                    <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                        <thead>
                            <tr>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>No.</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>User ID</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>First Name</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Last Name</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Email</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Phone</th>
                                <th style={{ border: '1px solid #ddd', padding: '8px' }}>Gender</th>
                            </tr>
                        </thead>
                        <tbody>
                            {searchUsers.map((user, index) => (
                                <tr key={user.userId}>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.romanIndex}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.userId}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.userFirstname}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.userLastname}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.username}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.phoneNumberWithCountryCode}</td>
                                    <td style={{ border: '1px solid #ddd', padding: '8px' }}>{user.gender}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            ) : (
                !loadingBar &&
                selectedCompany && <p>No users found for the company "{selectedCompany}".</p>
            )}
        </div>
    );
};

export default CompanyUsersComponent;
