import React, { useState, useEffect } from 'react';
import AdminService from '../../../../../Services/admin_service/AdminService';
import Constants from '../../../../Constants';
import { CDBDataTable } from 'cdbreact';

const CompanyUsersComponent = () => {
    const [enrolledCandidates, setEnrolledCandidates] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState("");
    const [showEnrolledCandidates, setShowEnrolledCandidates] = useState(false);
    const [loadingBar, setLoadingBar] = useState(false);
    const [errorMessageDiv, setErrorMessageDiv] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");
    const [companyName, setCompanyName] = useState(""); // State for company name

    const fetchCompaniesData = async () => {
        try {
            const response = await AdminService.getCompanies(); // Replace with actual API call
            console.log(response);
        } catch (error) {
            console.error("Error fetching companies:", error.message);
        }
    };

    const handleCloseCompanyModal = () => {
        fetchCompaniesData();
        setShowEnrolledCandidates(false);
        setSelectedCompany("");
        setEnrolledCandidates([]);
    };

    const fetchUsersWithCompanyName = async () => {
        if (!companyName.trim()) {
            displayErrorMessage("Please enter a valid company name");
            return;
        }

        setLoadingBar(true);
        const requestPayload = { companyName }; // Payload includes company name
        try {
            const responseData = await AdminService.fetchCandidatesByCompany(requestPayload);
            console.log(responseData);
            if (responseData.length === 0) {
                displayErrorMessage("No candidates found for this company");
            } else {
                setEnrolledCandidates(responseData);
                setSelectedCompany(companyName);
                setShowEnrolledCandidates(true);
            }
        } catch (error) {
            console.error(error.message);
            handleFetchCandidatesErrors(error.message);
        } finally {
            setLoadingBar(false);
        }
    };

    const handleFetchCandidatesErrors = (errorStatus) => {
        if (Constants.INVALID_REQUEST_FIELD === errorStatus)
            displayErrorMessage("The company name is invalid");
        else if (Constants.BREEZE_DATABASE_EXCEPTION === errorStatus)
            displayErrorMessage("Sorry, Our service is currently down");
        else displayErrorMessage("Could not process your request");
    };

    const displayErrorMessage = (message) => {
        setErrorMessage(message);
        setErrorMessageDiv(true);
        setTimeout(() => {
            setErrorMessage("");
            setErrorMessageDiv(false);
        }, Constants.SET_TIME_OUT_FOR_ERROR_MSG);
    };

    // Columns definition for the DataTable
    const columns = [
        { label: 'User ID', field: 'userId' },
        { label: 'First Name', field: 'userFirstname' },
        { label: 'Last Name', field: 'userLastname' },
        { label: 'Email', field: 'username' },
        { label: 'Phone', field: 'phoneNumberWithCountryCode' },
        { label: 'Gender', field: 'gender' },
        {
            label: 'Action',
            field: 'action',
            formatter: (candidateData) => (
                <div style={{ display: 'flex', gap: '10px' }}>
                    <button
                        className="enroll-status-confirmed-button"
                        onClick={() => handleAdminActionOnEnrolledCandidate(Constants.CONFIRMED, candidateData)}
                    >
                        Confirm
                    </button>
                    <button
                        className="enroll-status-withdraw-button"
                        onClick={() => handleAdminActionOnEnrolledCandidate(Constants.WITHDRAW, candidateData)}
                    >
                        Withdraw
                    </button>
                </div>
            ),
        },
    ];

    const handleAdminActionOnEnrolledCandidate = async (action, candidateData) => {
        setLoadingBar(true);
        const adminActionRequest = {
            adminStatus: action,
            candidateId: candidateData.userId,
            trainingId: selectedCompany, // Modify according to your context
        };
        try {
            const responseData = await AdminService.confirmOrWithdrawEnrolledCandidate(adminActionRequest);
            if (Constants.CONFIRMED === action) {
                displayErrorMessage('Successfully Confirmed Candidate');
            } else if (Constants.WITHDRAW === action) {
                displayErrorMessage('Successfully Withdrawn Candidate');
            }
        } catch (error) {
            console.log(error.message);
            handleAdminActionErrors(error.message);
        } finally {
            setLoadingBar(false);
        }
    };

    const handleAdminActionErrors = (errorStatus) => {
        if (Constants.INVALID_REQUEST_FIELD === errorStatus)
            displayErrorMessage("Inputs are invalid");
        else if (Constants.ENTITY_NOT_FOUND === errorStatus)
            displayErrorMessage("Entity Not Found");
        else if (Constants.BREEZE_DATABASE_EXCEPTION === errorStatus)
            displayErrorMessage("Sorry, Our service is down");
        else
            displayErrorMessage("Could not process your request");
    };

    return (
        <div>
            <h1>Company Users</h1>
            {errorMessageDiv && <div className="error-message">{errorMessage}</div>}

            <div style={{ marginBottom: "10px" }}>
                <label htmlFor="companyName" style={{ marginRight: "10px" }}>
                    Enter Company Name:
                </label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                    placeholder="Enter company name"
                    style={{
                        padding: "5px",
                        fontSize: "14px",
                        borderRadius: "5px",
                        border: "1px solid #ccc",
                        marginRight: "10px",
                    }}
                />
                <button
                    onClick={fetchUsersWithCompanyName}
                    style={{
                        padding: "5px 10px",
                        fontSize: "14px",
                        borderRadius: "5px",
                        backgroundColor: "#007bff",
                        color: "#fff",
                        border: "none",
                        cursor: "pointer",
                    }}
                >
                    Search
                </button>
            </div>

            {loadingBar && <p>Loading...</p>}

            {showEnrolledCandidates && (
                <div>
                    <h2>Enrolled Candidates for {selectedCompany}</h2>

                    {/* DataTable using CDBDataTable */}
                    <CDBDataTable
                        rows={enrolledCandidates}
                        columns={columns}
                        striped
                        bordered
                        hover
                    />
                </div>
            )}
        </div>
    );
};

export default CompanyUsersComponent;
