import React, { useState, useEffect } from 'react';
import AdminService from '../../../../../Services/admin_service/AdminService';
import Constants from '../../../../Constants';

const CompanyUsersComponent = () => {
    const [searchUsers, setSearchUsers] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState('');
    const [loadingBar, setLoadingBar] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [companyName, setCompanyName] = useState('');
    const [buttonsEnabled, setButtonsEnabled] = useState(false); // Manage button state

    // Fetch users by company name
    const fetchUsersWithCompanyName = async () => {
        if (!companyName.trim()) {
            displayErrorMessage('Please enter a valid company name.');
            return;
        }

        setLoadingBar(true);
        setErrorMessage(''); // Clear previous error messages
        try {
            const requestPayload = { companyName };
            const responseData = await AdminService.fetchCandidatesByCompany(requestPayload);

            if (!responseData || responseData.length === 0) {
                displayErrorMessage('No candidates found for this company.');
                setSearchUsers([]); // Empty the users list
                setButtonsEnabled(false); // Disable buttons
                return;
            }

            // Deduplicate users based on userId
            const uniqueData = responseData.reduce((acc, user) => {
                if (!acc.find((item) => item.userId === user.userId)) {
                    acc.push({
                        userId: user.userId,
                        name: `${user.userFirstname} ${user.userLastname}`, // Combine firstname and lastname into name
                        contact: `${user.phoneNumber} / ${user.username}`, // Combine phoneNumber and username into contact
                        userType: user.userType,
                        exponentType: user.exponentType,
                        userTechnicalStack: user.userTechincalStack, // Add technical stack field
                    });
                }
                return acc;
            }, []);
            
            // Update the searchUsers state with the unique data
            setSearchUsers(uniqueData);
            setSelectedCompany(companyName);
            setButtonsEnabled(true); // Enable buttons
        } catch (error) {
            console.error(error.message);
            handleFetchCandidatesErrors(error.message);
            setButtonsEnabled(false); // Disable buttons in case of error
        } finally {
            setLoadingBar(false);
        }
    };

    // Activate all users for the selected company
    const activateUsers = async () => {
        if (!selectedCompany) {
            displayErrorMessage('No company selected.');
            return;
        }

        setLoadingBar(true);
        setErrorMessage(''); // Clear previous error messages
        try {
            const requestPayload = { companyName: selectedCompany, activate: true };
            const response = await AdminService.updateUserStatusByCompany(requestPayload);

            if (response.success) {
                displayErrorMessage('All users have been activated successfully!');
                // Update UI to reflect the new state if necessary
                setSearchUsers((prevUsers) =>
                    prevUsers.map((user) => ({ ...user, status: 'active' }))
                );
            } else {
                displayErrorMessage('Failed to activate users. Please try again later.');
            }
        } catch (error) {
            console.error(error.message);
            displayErrorMessage('An unexpected error occurred. Please try again later.');
        } finally {
            setLoadingBar(false);
        }
    };

    // Display error message
    const displayErrorMessage = (message) => {
        setErrorMessage(message);
        setTimeout(() => setErrorMessage(''), Constants.SET_TIME_OUT_FOR_ERROR_MSG || 3000);
    };

    return (
        <div>
            <h1>Company Users</h1>

            {/* Display error message */}
            {errorMessage && <div className="error-message" style={{ color: 'red', marginBottom: '10px' }}>{errorMessage}</div>}

            {/* Input for company name and search button */}
            <div className="search-container" style={{ marginBottom: '20px' }}>
                <label htmlFor="companyName" style={{ marginRight: '10px' }}>Enter Company Name:</label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                    placeholder="Enter company name"
                    style={{
                        padding: '5px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        border: '1px solid #ccc',
                        marginRight: '10px',
                    }}
                />
                <button
                    onClick={fetchUsersWithCompanyName}
                    style={{
                        padding: '5px 10px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        cursor: 'pointer',
                        marginRight: '10px',
                    }}
                >
                    Search
                </button>

                {/* Active and Inactive buttons */}
                <button
                    onClick={activateUsers}
                    disabled={!buttonsEnabled}
                    style={{
                        padding: '5px 10px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        backgroundColor: buttonsEnabled ? '#28a745' : '#ccc',
                        color: '#fff',
                        border: 'none',
                        cursor: buttonsEnabled ? 'pointer' : 'not-allowed',
                        marginRight: '10px',
                    }}
                >
                    Active
                </button>
                <button
                    disabled={!buttonsEnabled}
                    style={{
                        padding: '5px 10px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        backgroundColor: buttonsEnabled ? '#dc3545' : '#ccc',
                        color: '#fff',
                        border: 'none',
                        cursor: buttonsEnabled ? 'pointer' : 'not-allowed',
                    }}
                >
                    Inactive
                </button>
            </div>

            {/* Show loading message while fetching */}
            {loadingBar && <p>Loading...</p>}

            {/* Display data table if there are users */}
            {searchUsers.length > 0 ? (
                <div>
                    <h2>Enrolled Users for {selectedCompany}</h2>
                    <table className="styled-table" style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                        <thead>
                            <tr>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>#</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>User ID</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>Name</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>Contact & MailId</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>User Type</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>Exponent Type</th>
                                <th style={{ padding: '10px', borderBottom: '2px solid #ccc', backgroundColor: '#f1f1f1' }}>Technical Stack</th>
                            </tr>
                        </thead>
                        <tbody>
                            {searchUsers.map((user, index) => (
                                <tr key={user.userId} style={{ borderBottom: '1px solid #ccc' }}>
                                    <td style={{ padding: '10px', borderRight: '1px solid #ccc', borderBottom: '1px solid #ccc' }}>{index + 1}</td>
                                    <td style={{ padding: '10px', borderRight: '1px solid #ccc', borderBottom: '1px solid #ccc' }}>{user.userId}</td>
                                    <td style={{ padding: '10px', borderRight: '1px solid #ccc', borderBottom: '1px solid #ccc' }}>{user.name}</td>
                                    <td style={{ padding: '10px', borderRight: '1px solid #ccc', borderBottom: '1px solid #ccc' }}>{user.contact}</td>
                                    <td style={{ padding: '10px', borderRight: '1px solid #ccc', borderBottom: '1px solid #ccc' }}>{user.userType}</td>
                                    <td style={{ padding: '10px', borderRight: '1px solid #ccc', borderBottom: '1px solid #ccc' }}>{user.exponentType}</td>
                                    <td style={{ padding: '10px', borderRight: '1px solid #ccc', borderBottom: '1px solid #ccc' }}>{user.userTechnicalStack}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            ) : (
                !loadingBar && selectedCompany && <p>No users found for the company "{selectedCompany}".</p>
            )}
        </div>
    );
};

export default CompanyUsersComponent;
