import React, { useState, useEffect } from 'react';



const CompanyUsersComponent = () => {
    const [enrolledCandidates, setEnrolledCandidates] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState(0);
    const [showEnrolledCandidates, setShowEnrolledCandidates] = useState(false);
    const [loadingBar, setLoadingBar] = useState(false);
    const [errorMessageDiv, setErrorMessageDiv] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");

    const fetchCompaniesData = async () => {
        try {
            const response = await AdminService.getCompanies(); // Replace with actual API call
            console.log(response);
        } catch (error) {
            console.error("Error fetching companies:", error.message);
        }
    };

    const handleCloseCompanyModal = () => {
        fetchCompaniesData();
        setShowEnrolledCandidates(false);
        setSelectedCompany(0);
        setEnrolledCandidates([]);
    };

    const fetchUsersWithCompanyName = async (companyId) => {
        setLoadingBar(true);
        const requestPayload = { companyId };
        try {
            const responseData = await AdminService.fetchCandidatesByCompany(requestPayload);
            console.log(responseData);
            if (responseData.length === 0) {
                displayErrorMessage('Candidates are not found');
            } else {
                setEnrolledCandidates(responseData);
                setSelectedCompany(companyId);
                setShowEnrolledCandidates(true);
            }
        } catch (error) {
            console.error(error.message);
            handleFetchCandidatesErrors(error.message, companyId);
        } finally {
            setLoadingBar(false);
        }
    };

    const handleFetchCandidatesErrors = (errorStatus, companyId) => {
        if (Constants.INVALID_REQUEST_FIELD === errorStatus)
            displayErrorMessage("Selected company is invalid");
        else if (Constants.BREEZE_DATABASE_EXCEPTION === errorStatus)
            displayErrorMessage("Sorry, Our service is down");
        else displayErrorMessage("Could not process your request");
    };

    const displayErrorMessage = (message) => {
        setErrorMessage(message);
        setErrorMessageDiv(true);
        setTimeout(() => {
            setErrorMessage("");
            setErrorMessageDiv(false);
        }, Constants.SET_TIME_OUT_FOR_ERROR_MSG);
    };

    return (
        <div>
            <h1>Company Users</h1>
            {errorMessageDiv && <div className="error-message">{errorMessage}</div>}
            <button onClick={() => fetchUsersWithCompanyName(1)}>Fetch Users for Company 1</button>
            {showEnrolledCandidates && (
                <div>
                    <h2>Enrolled Candidates</h2>
                    <pre>{JSON.stringify(enrolledCandidates, null, 2)}</pre>
                </div>
            )}
        </div>
    );
};

export default CompanyUsersComponent;
