import React, { useState } from 'react';
import AdminService from '../../../../../Services/admin_service/AdminService';
import Constants from '../../../../Constants';
import '../../../feature_component/fetchuser/CompanyUsersComponent.css';

// Helper function to deduplicate users and ensure uppercase status
const deduplicateUsers = (users) => {
    return users.reduce((acc, user) => {
        if (!acc.find((item) => item.userId === user.userId)) {
            acc.push({
                userId: user.userId,
                name: `${user.userFirstname} ${user.userLastname}`,
                contact: `${user.phoneNumber} / ${user.username}`,
                userType: user.userType,
                exponentType: user.exponentType,
                userTechnicalStack: user.userTechincalStack,
                status: user.status.toUpperCase(), // Convert status to uppercase
            });
        }
        return acc;
    }, []);
};

// UserTable component
const UserTable = ({ users }) => {
    if (!users || users.length === 0) {
        return <p>No users available.</p>;
    }

    return (
        <table className="styled-table" style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
            <thead>
                <tr>
                    <th>#</th>
                    <th>User ID</th>
                    <th>Name</th>
                    <th>Contact & MailId</th>
                    <th>User Type</th>
                    <th>Exponent Type</th>
                    <th>Technical Stack</th>
                    <th>Status</th>
                </tr>
            </thead>
            <tbody>
                {users.map((user, index) => (
                    <tr key={user.userId}>
                        <td>{index + 1}</td>
                        <td>{user.userId}</td>
                        <td>{user.name}</td>
                        <td>{user.contact}</td>
                        <td>{user.userType}</td>
                        <td>{user.exponentType}</td>
                        <td>{user.userTechnicalStack}</td>
                        <td>{user.status}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

// FilterButtons component
const FilterButtons = ({ statusFilter, onFilterChange }) => {
    return (
        <div style={{ marginBottom: '20px' }}>
            <button
                onClick={() => onFilterChange('ACTIVE')}
                style={{
                    padding: '5px 10px',
                    fontSize: '14px',
                    borderRadius: '5px',
                    backgroundColor: statusFilter === 'ACTIVE' ? '#007bff' : '#ccc',
                    color: '#fff',
                    border: 'none',
                    cursor: 'pointer',
                    marginRight: '10px',
                }}
            >
                ACTIVE
            </button>
            <button
                onClick={() => onFilterChange('INACTIVE')}
                style={{
                    padding: '5px 10px',
                    fontSize: '14px',
                    borderRadius: '5px',
                    backgroundColor: statusFilter === 'INACTIVE' ? '#007bff' : '#ccc',
                    color: '#fff',
                    border: 'none',
                    cursor: 'pointer',
                }}
            >
                INACTIVE
            </button>
        </div>
    );
};

// Main component
const CompanyUsersComponent = () => {
    const [searchUsers, setSearchUsers] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState('');
    const [loadingBar, setLoadingBar] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [companyName, setCompanyName] = useState('');
    const [statusFilter, setStatusFilter] = useState('ALL');

    const displayErrorMessage = (message) => {
        setErrorMessage(message);
        setSuccessMessage('');
        setTimeout(() => setErrorMessage(''), Constants.SET_TIME_OUT_FOR_ERROR_MSG || 3000);
    };

    const displaySuccessMessage = (message) => {
        setSuccessMessage(message);
        setErrorMessage('');
        setTimeout(() => setSuccessMessage(''), 5000);
    };

    const fetchUsersWithCompanyName = async () => {
        if (!companyName.trim()) {
            displayErrorMessage('Please enter a valid company name.');
            return;
        }

        setLoadingBar(true);
        setErrorMessage('');
        setSuccessMessage('');
        try {
            const requestPayload = { companyName };
            const responseData = await AdminService.fetchCandidatesByCompany(requestPayload);

            if (!responseData || responseData.length === 0) {
                displayErrorMessage('No candidates found for this company.');
                setSearchUsers([]);
                return;
            }

            const uniqueData = deduplicateUsers(responseData);
            setSearchUsers(uniqueData);
            setSelectedCompany(companyName);
        } catch (error) {
            console.error(error.message);
            displayErrorMessage('An unexpected error occurred. Please try again later.');
        } finally {
            setLoadingBar(false);
        }
    };

    const filterUsersByStatus = async (status) => {
        const upperCaseStatus = status.toUpperCase(); // Ensure status is always uppercase
        setStatusFilter(upperCaseStatus);

        if (!companyName.trim()) {
            displayErrorMessage('Please enter a valid company name.');
            return;
        }

        setLoadingBar(true);
        setErrorMessage('');
        setSuccessMessage('');

        try {
            await AdminService.updateUserStatusByCompany(companyName, upperCaseStatus);
            displaySuccessMessage(`Successfully changed company status to ${upperCaseStatus}.`);

            // Refresh user data
            fetchUsersWithCompanyName();
        } catch (error) {
            console.error(error.message);
            displayErrorMessage('An unexpected error occurred. Please try again later.');
        } finally {
            setLoadingBar(false);
        }
    };

    const filteredUsers = searchUsers.filter((user) => {
        if (statusFilter === 'ACTIVE') {
            return user.status === 'ACTIVE';
        } else if (statusFilter === 'INACTIVE') {
            return user.status === 'INACTIVE';
        }
        return true;
    });

    return (
        <div>
            <h1>Company Users</h1>

            {/* Error and Success Messages */}
            {errorMessage && <div className="error-message" style={{ color: 'red', marginBottom: '10px' }}>{errorMessage}</div>}
            {successMessage && <div className="success-message" style={{ color: 'green', marginBottom: '10px' }}>{successMessage}</div>}

            {/* Filter Buttons */}
            <FilterButtons statusFilter={statusFilter} onFilterChange={filterUsersByStatus} />

            {/* Search Box */}
            <div className="search-container" style={{ marginBottom: '20px' }}>
                <label htmlFor="companyName" style={{ marginRight: '10px' }}>Enter Company Name:</label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                    placeholder="Enter company name"
                    style={{
                        padding: '5px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        border: '1px solid #ccc',
                        marginRight: '10px',
                    }}
                />
                <button
                    onClick={fetchUsersWithCompanyName}
                    style={{
                        padding: '5px 10px',
                        fontSize: '14px',
                        borderRadius: '5px',
                        backgroundColor: '#007bff',
                        color: '#fff',
                        border: 'none',
                        cursor: 'pointer',
                    }}
                >
                    Search
                </button>
            </div>

            {/* Loading Indicator */}
            {loadingBar && <p>Loading...</p>}

            {/* Users Table */}
            {filteredUsers.length > 0 ? (
                <div>
                    <h2>Enrolled Users for {selectedCompany}</h2>
                    <UserTable users={filteredUsers} />
                </div>
            ) : (
                !loadingBar && selectedCompany && <p>No users found for the company "{selectedCompany}".</p>
            )}
        </div>
    );
};

export default CompanyUsersComponent;
